import React, { useState, useEffect } from "react";
import '../../App.css';
import CatBox from './CatBox';
import { Link, Route, Routes } from "react-router-dom";
import './Category.css';
import { getClubs } from '../../services/club-services';
import SinglePlayer from "./SinglePlayer";


function Category() {
    var str = window.location.pathname.substring(1);
    var s = str;
    console.log(`${s}/sport`);

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [clubs, setClubs] = useState(null);

    const numbers = [1, 2, 3, 4, 5, 7, 8];
    const text = ['Sports', 'History', 'Geography', 'Science and Math', 'Literature', 'Movies and Series', 'gk', 'Space'];
    const label = ['sport', 'history', 'geography', 'science', 'literature', 'movies', 'GK', 'space'];

    const listItems = numbers.map((number) =>
        <li>{number}</li>
    );

    useEffect(() => {
        const getData = async () => {
            setLoading(true);
            await getClubs().then(data => {
                setClubs(data);
                setLoading(false);
            })
        }
        getData();
    }, [])

    if (error) return <h1>Error</h1>
    if (loading) return <h1>Loading....</h1>

    return (


        <>
            <div className='cards'>
                {label && label.map(label => {
                    return <Link  to={`/club/${club.id}`}>

                         <CatBox

                                src={window.location.origin + '/images/category/cat_1.png'}
                                text={`${text}`}
                                label={`${texlabel}`}
                                path={`/${s}/${label}`}

                            />   

                    </Link>
                })}

                <h1>Quiz Categories</h1>
                <div className='cards__container'>
                    <div className='cards__wrapper'>
                        <ul className='cards__items'>



                            <CatBox

                                src={window.location.origin + '/images/category/cat_1.png'}
                                text="Sports"
                                label="sport"
                                path={`/${s}/sport`}

                            />

                            <CatBox
                                src={window.location.origin + '/images/category/cat_2.png'}
                                text="History"
                                label="history"
                                path={`/${s}/history`}
                            />


                        </ul>
                    </div>
                </div>

                <div className='cards__container'>
                    <div className='cards__wrapper'>
                        <ul className='cards__items'>
                            <CatBox
                                src={window.location.origin + '/images/category/cat_3.png'}
                                text="Geography"
                                label="geography"
                                path={`/${s}/geography`}
                            />
                            <CatBox
                                src={window.location.origin + '/images/category/cat_4.png'}
                                text="Science and Math"
                                label="science"
                                path={`/${s}/science`}
                            />

                        </ul>
                    </div>
                </div>
                <div className='cards__container'>
                    <div className='cards__wrapper'>
                        <ul className='cards__items'>

                            <CatBox
                                src={window.location.origin + '/images/category/cat_5.png'}
                                text="Literature"
                                label="literature"
                                path={`/${s}/literature`}
                            />
                            <CatBox
                                src={window.location.origin + '/images/category/cat_6.png'}
                                text="Movies and Series"
                                label="movies"
                                path={`/${s}/movies`}
                            />
                        </ul>
                    </div>
                </div>
                <div className='cards__container'>
                    <div className='cards__wrapper'>
                        <ul className='cards__items'>

                            <CatBox
                                src={window.location.origin + '/images/category/cat_7.png'}
                                text="GK"
                                label="GK"
                                path={`/${s}/GK`}
                            />
                            <CatBox
                                src={window.location.origin + '/images/category/cat_8.png'}
                                text="Space"
                                label="Space"
                                path={`/${s}/Space`}
                            />

                        </ul>
                    </div>
                </div>
            </div>
        </>


    );
}
export default Category;
