import React, { useState } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { Button } from '../Button';
import { useNavigate } from "react-router-dom";
import { AddClub } from '../../services/club-services';
import { Link } from "react-router-dom";

import { NotificationManager } from 'react-notifications';
import AccountCircle from '@mui/icons-material/AccountCircle';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import LockIcon from '@mui/icons-material/Lock';
import { AddQuestion } from '../../services/quiz-services';


export default function ChangeCategory() {
  const [ques_type, setQues_type] = useState('');
  const [category, setCategory] = useState('');
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [point, setPoint] = useState('');
  const [newCategory, setNewCategory] = useState('');

  const { authData, setAuth } = useAuth();
  const navigate = useNavigate();


  const handleSubmit = async e => {
    e.preventDefault();
    console.log("Adding question");
    const uploaded = await AddQuestion(
      ques_type,
      newCategory,
      question,
      answer,
      point
    );
    if (uploaded) {
      NotificationManager.success("Question added successfully");
      navigate('/questionspage');
    }
    else {
      NotificationManager.error("Error adding question");
    }
  }


  return (


    <div className='clubinput'>
      <div>
        <h1>Change Category</h1>
        <form >

          <Box sx={{ display: 'flex', alignItems: 'flex-end' }}>
            <TextField id="input-with-sx" label="Existing Category" variant="standard"
              onChange={e => setCategory(e.target.value)}
            />
          </Box>

          <Box sx={{ display: 'flex', alignItems: 'flex-end' }}>
            <TextField id="input-with-sx" label="New Category" variant="standard"
              onChange={e => setNewCategory(e.target.value)}
            />
          </Box>

          
          <div className="logging">
            <Button type='submit' onClick={handleSubmit}>
              Add New Question with New Category
            </Button>
          </div>
        </form>
      </div>

    </div>
  );
}
