import React, { Component } from 'react'

class Counter extends Component {

  constructor(props) {
    super(props)
    this.state = {
      count: 0
    }
  }

  incrementCount() {
    this.setState(
      {
        count: this.state.count + 1
      }
    )
  }
  decrementCount() {
    this.setState(
      {
        count: this.state.count - 1
      }
    )
    ,
    () =>{
        console.log('Call back value', this.state.count)
    }

  }

  render() {
    return (
      <div>
        <div>count : {this.state.count}</div>
        <div><button onClick={() => this.incrementCount()}> Increment</button></div>
        <div><button onClick={() => this.decrementCount()}> Decrement</button></div>
      </div>
    )
  }
}

export default Counter
