import React, { Component } from "react";
class Message extends Component{

    constructor(){
        var x = 0
        super()
        this.state = {
            message  : "Welcome to our site"
        }

    }
    changeMessage(){
        this.setState(
            {
                message:"Thank you for subscribing"
            }
        )
        
    }
    render(){
        return (
            <div>
            <h1>
                {this.state.message}
            </h1>
            <button onClick={() => this.changeMessage() }>Subscribe</button>
            </div>

        )
    }

}
export default Message
