import React, { useState, useEffect } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { Button } from '../Button';
import { useNavigate } from "react-router-dom";
import { AddClub } from '../../services/club-services';
import { Link } from "react-router-dom";

import { NotificationManager } from 'react-notifications';
import AccountCircle from '@mui/icons-material/AccountCircle';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import LockIcon from '@mui/icons-material/Lock';
import { AddQuestion } from '../../services/quiz-services';
import { getQuestions } from '../../services/quiz-services';


export default function ChangeCategory() {
    const [ques_type, setQues_type] = useState('');
    const [category, setCategory] = useState('');
    const [question, setQuestion] = useState('');
    const [answer, setAnswer] = useState('');
    const [point, setPoint] = useState('');
    const [newCategory, setNewCategory] = useState('');
    const [questionList, getAllQuestions] = useState(null);

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const { authData, setAuth } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        const getData = async () => {
            setLoading(true);
            await getQuestions().then(data => {
                getAllQuestions(data);
                setLoading(false);
            })
        }
        getData();
    }, [])


    //   const handleSubmit = async e => {
    //     e.preventDefault();
    //     console.log("Adding question");
    //     const uploaded = await AddQuestion(
    //       ques_type,
    //       newCategory,
    //       question,
    //       answer,
    //       point
    //     );
    //     if (uploaded) {
    //       NotificationManager.success("Question added successfully");
    //       navigate('/questionspage');
    //     }
    //     else {
    //       NotificationManager.error("Error adding question");
    //     }
    //   }

    const handleSubmit = async e => {
        e.preventDefault();
        console.log("Adding question");
        for (var i = 0; i < questionList.length; i++) {
            if (questionList[i].category === category) {
                setQues_type(questionList[i].ques_type);
                setQuestion(questionList[i].question);
                setAnswer(questionList[i].answer);
                setPoint(questionList[i].point);

                console.log(questionList[i].ques_type);
                console.log(questionList[i].question);
                console.log(questionList[i].answer);
                console.log(questionList[i].point);

                const uploaded = await AddQuestion(
                    ques_type,
                    newCategory,
                    question,
                    answer,
                    point
                );

                if (uploaded) {
                    NotificationManager.success("Question added successfully");
                    navigate('/questionspage');
                }
                else {
                    NotificationManager.error("Error adding question");
                }

            }
        }
    }


    return (


        <div className='clubinput'>
            <div>
                <h1>Change Category</h1>
                <form >

                    <Box sx={{ display: 'flex', alignItems: 'flex-end' }}>
                        <TextField id="input-with-sx" label="Existing Category" variant="standard"
                            onChange={e => setCategory(e.target.value)}
                        />
                    </Box>

                    <Box sx={{ display: 'flex', alignItems: 'flex-end' }}>
                        <TextField id="input-with-sx" label="New Category" variant="standard"
                            onChange={e => setNewCategory(e.target.value)}
                        />
                    </Box>


                    <div className="logging">
                        <Button type='submit' onClick={handleSubmit}>
                            Add New Question with New Category
                        </Button>
                    </div>
                </form>
            </div>

        </div>
    );
}
